var chalk=require("chalk"),timestamp=require("time-stamp");function getTimestamp(){return"["+chalk.grey(timestamp("HH:mm:ss"))+"]"}function log(){var a=getTimestamp();process.stdout.write(a+" ");console.log.apply(console,arguments);return this}function info(){var a=getTimestamp();process.stdout.write(a+" ");console.info.apply(console,arguments);return this}function dir(){var a=getTimestamp();process.stdout.write(a+" ");console.dir.apply(console,arguments);return this}
function warn(){var a=getTimestamp();process.stderr.write(a+" ");console.warn.apply(console,arguments);return this}function error(){var a=getTimestamp();process.stderr.write(a+" ");console.error.apply(console,arguments);return this}module.exports=log;module.exports.info=info;module.exports.dir=dir;module.exports.warn=warn;module.exports.error=error;
