var parse=require("../"),test=require("tape");test("flag boolean true (default all --args to boolean)",function(a){var b=parse(["moo","--honk","cow"],{boolean:!0});a.deepEqual(b,{honk:!0,_:["moo","cow"]});a.deepEqual(typeof b.honk,"boolean");a.end()});
test("flag boolean true only affects double hyphen arguments without equals signs",function(a){var b=parse("moo --honk cow -p 55 --tacos=good".split(" "),{boolean:!0});a.deepEqual(b,{honk:!0,tacos:"good",p:55,_:["moo","cow"]});a.deepEqual(typeof b.honk,"boolean");a.end()});
