var test=require("tap").test,semver=require("../");test("long version is too long",function(a){var b="1.2."+Array(256).join("1");a.throws(function(){new semver.SemVer(b)});a.equal(semver.valid(b,!1),null);a.equal(semver.valid(b,!0),null);a.equal(semver.inc(b,"patch"),null);a.end()});
test("big number is like too long version",function(a){var b="1.2."+Array(100).join("1");a.throws(function(){new semver.SemVer(b)});a.equal(semver.valid(b,!1),null);a.equal(semver.valid(b,!0),null);a.equal(semver.inc(b,"patch"),null);a.end()});test("parsing null does not throw",function(a){a.equal(semver.parse(null),null);a.equal(semver.parse({}),null);a.equal(semver.parse(new semver.SemVer("1.2.3")).version,"1.2.3");a.end()});
